import { faList } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    Container
} from "@mui/material";
import { DefaultCard } from "components/card";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import { UrlBuilder } from "../../../../helpers/UrlBuilder";
import { callApi, clearState, selectApi } from "../../../../reducers/apiSlice";
//import { values } from "core-js/core/array";
import { Button } from "@themesberg/react-bootstrap";
import InstituteTypeReviewer from "constants/InstituteTypeReviewer";
import UserRole from "constants/UserRole";
import { camelCaseToTitle } from "helpers/CameltoTitle";
import moment from "moment";
import { useRef } from "react";
import { Step, Stepper } from "react-form-stepper";
import environment from "../../../../components/environments/environment";
import ApplicationType from "../../../../constants/ApplicationType";
import ScoringMethodType from "../../../../constants/ScoringMethodType";
import { AuthUser } from "../../../../helpers/AuthUser";
import "./Approval.scss";
import ReviewView from "./shared/ReviewView";
import TeacherDetailView from "./shared/TeacherDetailView";

const ListFilter = ({
    editMode,
    instituteId,
    initialValues,
    changeStep,
    nextStep,
    ...props
}) => {
    let rolesID = AuthUser.getRolesID();
    const [reviewer, setReviewer] = useState();
    const [reviewFormShow, setReviewFormShow] = useState(true);
    let roles = AuthUser.getRoles();

    /**
     * useHistory: navigation helper
     */
    const history = useHistory();

    /**
     * useDispatch: dispatch actions
     */
    const dispatch = useDispatch();

    /**
     * Get loading indicator and data from 'selectApi' state
     */
    const {
        loading,
        indexDetailsInfo = {
            data: {},
        },
        instituteReviews = {
            data: {},
        },
        reasonForRejections = {
            data: {},
        },
        reviewerList = {
            data: {},
        },
        indexApplicationEncloserReview = {
            data: {},
            status: "",
        },
        saveReview = {
            data: {},
            status: "",
        },
    } = useSelector(selectApi);

    const printRef = useRef();

    const getFile = (imgs) => {
        return environment.fileBaseUrl + imgs;
    };

    const cardProps = {
        title: "বিমুক্তির  অনুমোদন",
        headerSlot: () => (
            <Link to="/portal/application/index/approval">
                <Button variant="link" className="f-right btn-sm btn-color">
                    <FontAwesomeIcon icon={faList} className="me-2" />
                    বিমুক্তির অনুমোদনের তালিকা
                </Button>
            </Link>
        ),
    };

    useEffect(() => {
        if (
            indexDetailsInfo &&
            indexDetailsInfo.status == "success" &&
            indexDetailsInfo.data != undefined
        ) {
            if (indexDetailsInfo.data.activeEmployeeEducationLevelId == 64 || indexDetailsInfo.data.activeEmployeeEducationLevelId == 37)
                setReviewer(InstituteTypeReviewer.SCHOOL);
            else {
                setReviewer(InstituteTypeReviewer.SCHOOL_COLLEGE);

            }

        }
    });

    useEffect(() => {
        dispatch(
            callApi({
                operationId: UrlBuilder.mpoDsheApi(
                    `index-delete-release-request/find/${props?.location?.search.slice(1)}/${props.match.params.id}?applicationType=${ApplicationType.INDEX_RELEASE}`
                ),
                output: "indexDetailsInfo",
                storeName: "indexDetailsInfo",
            })
        );

        // get reason for rejection information
        dispatch(
            callApi({
                operationId: UrlBuilder.commonApi("reason-for-rejection/all"),
                output: "reasonForRejections",
                storeName: "reasonForRejections",
            })
        );
        dispatch(
            callApi({
                operationId: UrlBuilder.reviewerApi(
                    `approve-review-config/find/forAppEntityId/${reviewer}`
                ),
                output: "reviewerList",
                storeName: "reviewerList",
            })
        );
        setReviewFormShow(true);
    }, [dispatch, props.match.params.id, reviewer]);

    useEffect(() => {
        if (indexApplicationEncloserReview.status == "success") {
            dispatch(
                clearState({
                    output: "indexApplicationEncloserReview",
                })
            );

            dispatch(
                callApi({
                    operationId: UrlBuilder.mpoDsheApi(
                        `index-delete-release-request/find/${props?.location?.search?.slice(1)}/${props.match.params.id}?applicationType=${ApplicationType.INDEX_RELEASE}`
                    ),
                    output: "indexDetailsInfo",
                    storeName: "indexDetailsInfo",
                })
            );
        }
        if (saveReview.status == "success") {
            dispatch(
                clearState({
                    output: "saveReview",
                })
            );

            dispatch(
                callApi({
                    operationId: UrlBuilder.mpoDsheApi(
                        `index-delete-release-request/find/${props?.location?.search?.slice(1)}/${props.match.params.id}?applicationType=${ApplicationType.INDEX_RELEASE}`
                    ),
                    output: "indexDetailsInfo",
                    storeName: "indexDetailsInfo",
                })
            );
        }
    });



    const checkEncloserValidity = () => {
        let checkStatus = [];

        if (
            indexDetailsInfo?.data?.employeeIndexDeleteReleaseEncloserList
                ?.length > 0
        ) {
            indexDetailsInfo?.data?.employeeIndexDeleteReleaseEncloserList.forEach(
                (item) => {
                    if (item.encloserReviewers?.length > 0) {
                        item.encloserReviewers.forEach((reviwer) => {
                            if (
                                reviwer.reviewerRoleId ===
                                AuthUser.getRoleID() &&
                                reviwer.reviewStatus === "VIEWED"
                            ) {
                                checkStatus.push(true);
                            }
                        });
                    }
                }
            );
        }

        if (roles.includes(UserRole.DSHE_DG)) {
            checkStatus.push(true)

        }

        if (
            checkStatus.length ===
            indexDetailsInfo?.data?.employeeIndexDeleteReleaseEncloserList
                ?.length
        ) {
            return checkStatus;
        } else {
            return roles.includes(UserRole.DSHE_DG) ? checkStatus : [];
        }
    };


    let currentRole = indexDetailsInfo && indexDetailsInfo.data && indexDetailsInfo.data?.currentApproverReviewerRoleName
    let currentStep = reviewerList && reviewerList.data && reviewerList.data?.approverReviewerUsers && reviewerList.data?.approverReviewerUsers.length > 0 &&
        reviewerList.data?.approverReviewerUsers.findIndex(item => {
            return item.userRoleName === currentRole
        });


    return (
        <DefaultCard title="বিমুক্তির বিস্তারিত" {...cardProps}>
            <div className="container-fluid">
                <div className="col-12">
                    <span className="fw-bold fs-4 ml-20">
                        শিক্ষক/কর্মকর্তা/কর্মচারী ইনডেক্স আইডি : {indexDetailsInfo.data?.indexNo || "N/A"}
                    </span>
                </div>
                <hr></hr>
                <div>
                    <Stepper activeStep={currentStep} nonLinear={true}>
                        {reviewerList &&
                            reviewerList.data &&
                            reviewerList.data?.approverReviewerUsers &&
                            reviewerList.data?.approverReviewerUsers.length > 0 &&
                            reviewerList.data?.approverReviewerUsers.map(
                                (item, index) => {
                                    return (
                                        <Step
                                            className={
                                                currentStep == index
                                                    ? ""
                                                    : "bg-success"
                                            }
                                            label={camelCaseToTitle(item.userRoleName)?.toUpperCase()}
                                        ></Step>
                                    );
                                }
                            )}
                    </Stepper>
                </div>

                <div class="table-responsive" style={{ padding: "0 7%" }}>
                    <table class="table table-striped table-bordered table-hover ">
                        <tbody>
                            <tr>
                                <td className="wd-350"><b>আবেদন গ্রহণের তারিখ : </b></td>
                                <td className="wd-350">{moment(indexDetailsInfo?.data?.dateOfRequest).format("DD-MM-YYYY") || "N/A"}</td>
                                <td className="wd-350"><span><b>আবেদন মূল্যায়নের শেষ তারিখ : </b></span></td>
                                <td className="wd-350">   {indexDetailsInfo.data?.applicationReviewLastDate || "N/A"} </td>
                            </tr>

                        </tbody>
                    </table>
                </div>
                <div className="contentBoxBody mt-20"></div>
                <div className="row pt-3" autoFocus={true}>

                    <div className="col-lg-12">
                        <div className="contentBox-modified m-0">
                            <div className="section-header-custom">
                                <h5 className="m-10 text-white">অনুমোদনের বিবরণ</h5>
                            </div>
                            <div className="contentBoxBody mt-20"></div>
                            <div className="table-responsive">
                                <table className="table table-striped table-bordered table-hover ">
                                    <thead className="clr ">
                                        <th className="text-center">ক্রমিক নং</th>
                                        <th className="text-center">অনুমোদনকারীর  পদবি</th>
                                        <th className="text-center">গ্রহণের তারিখ</th>
                                        <th className="text-center">মূল্যায়নের  তারিখ</th>
                                        <th className="text-center">অনুমোদন প্রক্রিয়ার অবস্থা</th>
                                        <th className="text-center">মন্তব্য</th>

                                    </thead>

                                    <tbody>
                                        {indexDetailsInfo && indexDetailsInfo?.data?.indexDeleteReleaseApplicationReviewerList && indexDetailsInfo?.data?.indexDeleteReleaseApplicationReviewerList.length > 0 ? indexDetailsInfo?.data?.indexDeleteReleaseApplicationReviewerList.map((item, index) => {

                                            return (
                                                <tr>
                                                    <td className="text-center">{index + 1}</td>
                                                    <td className="text-center">{camelCaseToTitle(item.reviewerRoleName)?.toUpperCase()}</td>
                                                    <td className="text-center">{item.reviewerDate}</td>
                                                    <td className="text-center">{item.reviewerDate}</td>
                                                    <td className="text-center">{item.reviewStatus.replace(/_/g, " ")}</td>
                                                    <td className="text-center">{item.reviewerNote}</td>
                                                </tr>
                                            )

                                        }) : <tr className="text-center">
                                            <td className="text-center" colSpan="8">No Data</td>
                                        </tr>}



                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>

                    <div className="col-lg-10">
                        <div className="contentBox-modified m-0">
                            <div className="section-header-custom">
                                <h5 className="m-10 text-white">সাধারণ তথ্য</h5>
                            </div>
                            <div className="contentBoxBody mt-20"></div>
                            <div className="table-responsive">
                                <table className=" tb  table-hover-custom table-responsive table table-striped table-bordered table-hover  ">
                                    <tbody>
                                        <tr>
                                            <td>শিক্ষক/কর্মকর্তার নাম:</td>
                                            <td>
                                                {indexDetailsInfo.data?.applicantName ||
                                                    "N/A"}
                                            </td>
                                            <td>শিক্ষক/কর্মকর্তার ধরন:</td>
                                            <td>
                                                {indexDetailsInfo.data?.employeeTypeStatus?.replace(
                                                    /_/g,
                                                    " "
                                                ) || "N/A"}
                                            </td>
                                        </tr>
                                        <tr>

                                            <td>প্রতিষ্ঠান এম পি ও কোড:</td>
                                            <td>
                                                {(indexDetailsInfo &&
                                                    indexDetailsInfo.data &&
                                                    indexDetailsInfo.data
                                                        ?.instituteMPOCode) ||
                                                    "N/A"}
                                            </td>
                                            <td>লিঙ্গ :</td>
                                            <td>
                                                {(indexDetailsInfo.data &&
                                                    indexDetailsInfo.data?.gender) ||
                                                    "N/A"}
                                            </td>
                                        </tr>
                                        <tr>

                                            <td>যোগদানের তারিখ :</td>
                                            <td>
                                                {indexDetailsInfo.data &&
                                                    indexDetailsInfo.data?.joiningDate
                                                    ? moment(
                                                        indexDetailsInfo.data &&
                                                        indexDetailsInfo.data
                                                            ?.joiningDate
                                                    ).format("DD-MM-YYYY")
                                                    : "N/A"}
                                            </td>
                                        </tr>
                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                    <div className="col-lg-2">
                        <div className="img-avr">
                            <img
                                src={getFile(
                                    indexDetailsInfo.data?.profileImage?.encloserUrl
                                )}

                                className=" mt-30"
                                alt=""
                            ></img>
                        </div>
                    </div>
                    <div className="contentBoxBody mt-20"></div>
                    <div className="col-lg-12">
                        <div className="contentBox-modified m-0">
                            <div className="section-header-custom">
                                <h5 className="m-10 text-white"> ব্যাক্তিগত তথ্য</h5>
                            </div>
                            <div className="contentBoxBody mt-20"></div>
                            <div className="table-responsive">

                                <table className=" table-hover-custom table-responsive table table-striped table-bordered table-hover  ">
                                    <tbody>
                                        <tr>
                                            <td>পিতার  নাম (ইংরেজী):</td>

                                            <td>
                                                {indexDetailsInfo.data?.fathersName || "N/A"}
                                            </td>
                                            <td>মাতার  নাম (ইংরেজী):</td>
                                            <td>
                                                {indexDetailsInfo.data?.mothersName || "N/A"}
                                            </td>
                                        </tr>
                                        <tr>
                                            <td>জন্মতারিখ :</td>
                                            <td>{indexDetailsInfo.data?.dateOfBirth}</td>
                                            <td>ধর্ম :</td>
                                            <td>
                                                {indexDetailsInfo.data?.religion}
                                            </td>

                                        </tr>
                                        <tr>

                                            <td>জাতীয় পরিচয়পত্র নম্বর :</td>
                                            <td>{indexDetailsInfo.data?.nid || "N/A"}</td>
                                            <td>মোবাইল নম্বর :</td>
                                            <td>{indexDetailsInfo.data?.contactNumber || "N/A"}</td>
                                        </tr>
                                        <tr>

                                            <td>ই-মেইল :</td>
                                            <td>{indexDetailsInfo.data?.email || "N/A"}</td>
                                            <td>নিজ জেলা :</td>
                                            <td>
                                                {indexDetailsInfo.data?.homeDistrict || "N/A"}
                                            </td>
                                        </tr>

                                    </tbody>
                                </table>
                            </div>
                        </div>
                    </div>
                    {/* 2nd row starts */}
                    <div className="col-lg-12">
                        <div className="contentBox-modified m-0">
                            <div className="section-header-custom">
                                <h5 className="m-10 text-white">   শিক্ষাগত যোগ্যতা</h5>
                            </div>
                            <div className="contentBoxBody mt-20"></div>
                            <div className="table-responsive">
                                <table className="table table-striped table-bordered table-hover  table-hover-custom">
                                    <thead className="clr ">
                                        <tr>
