import * as Yup from 'yup';

class EmployeeModel {

	/**
	 * Model properties
	 */
	constructor() {
		this.employeeName = '';
		this.employeeNameBn = '';
		this.employeeId = '';
		this.employeeDesignation = '';
		this.employeePhone = '';
		this.employeeEmail = '';
		this.employeeAddress = '';
	}

	/**
	 * Get model instance from json
	 */
	fromJson(data = {}) {
		let obj = new EmployeeModel();
		if (data.id !== undefined && data.id) {
			obj.id = data.id;
		}
		obj.employeeName = data.countryName ?? '';
		obj.employeeNameBn = data.countryNameBn ?? '';
		obj.employeeId = data.countryIdLegacy ?? '';
		obj.employeeDesignation = data.countryIdLegacy ?? '';
		obj.employeePhone = data.countryIdLegacy ?? '';
		obj.employeeEmail = data.countryIdLegacy ?? '';
		obj.employeeAddress = data.countryIdLegacy ?? '';
		return obj;
	}

	/**
	 * Get string from model instance
	 */
	toString(data = {}) {
		let obj = new EmployeeModel()
			.fromJson(data);
		return JSON.stringify(obj);
	}

	/**
	 * Validator schema
	 */
	validator() {
		return Yup.object().shape({
			countryName: Yup.string()
				.min(3, 'Too Short!')
				.max(50, 'Too Long!')
				.required('Required'),
			countryNameBn: Yup.string()
				.min(3, 'Too Short!')
				.max(50, 'Too Long!')
				.required('Required')
		});
	}

}

export const Employee = new EmployeeModel();
